import React, { useContext, useState } from 'react'
import CardsShop from '../components/shop/CardsShop'

const NumberCard = React.createContext()
const NumberCardDispatcher = React.createContext()

const CardShop = React.createContext()
const CardShopDispatcher = React.createContext()

const Cards = React.createContext()
const CardsDispatcher = React.createContext()

const infoCard = [
    { id: 1, name: 'Hossein', price: 1200, qty: 1, info: 'This card for hossein', disabled: false, imgCard: 'data:image/jpeg;base64,' },
    { id: 2, name: 'Mohammad', price: 2400, qty: 1, info: 'This card for mohammad', disabled: false, imgCard: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Voria', price: 900, qty: 1, info: 'This card for voria', disabled: false, imgCard: 'data:image/jpeg;base64,' },
    { id: 4, name: 'Amir', price: 2900, qty: 1, info: 'This card for amir', disabled: false, imgCard: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSZMD2Ysbbe8bCEH4vEdS_YMavEejj8u3bPrA&usqp=CAU' },
    { id: 5, name: 'Amin', price: 1100, qty: 1, info: 'This card for amin', disabled: false, imgCard: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTgfrzi1gX9kX4AQ4GcSjlbQNoN5puP78RGqw&usqp=CAU' },
]

function ShopProviderContext({ children }) {

    const [number, setNumber] = useState(0)
    const [cards, setCards] = useState(infoCard)
    const [contentCards, setContentCards] = useState([])

    return (
        <div>
            <NumberCard.Provider value={number}>
                <NumberCardDispatcher.Provider value={setNumber}>
                    <CardShop.Provider value={cards}>
                        <CardShopDispatcher.Provider value={setCards}>
                            <Cards.Provider value={contentCards}>
                                <CardsDispatcher.Provider value={setContentCards}>
                                    {children}
                                </CardsDispatcher.Provider>
                            </Cards.Provider>
                        </CardShopDispatcher.Provider>
                    </CardShop.Provider>
                </NumberCardDispatcher.Provider>
            </NumberCard.Provider>
        </div>
    )
}

export default ShopProviderContext


export const UseNumberCard = () => useContext(NumberCard)
export const UseCardShop = () => useContext(CardShop)
export const UseCard = () => useContext(Cards)

export const UseNumberCardDispatcher = () => {
    const number = useContext(NumberCard)
    const setNumber = useContext(NumberCardDispatcher)

    const incrementCard = () => {
        setNumber(number + 1)
    }

    const decrementCard = () => {
        setNumber(number - 1)
    }

    return { incrementCard, decrementCard }
}

export const UseCardDispatcher = () => useContext(CardsDispatcher)

export const UseCardShopDispatcher = () => {
    let card = useContext(CardShop)
    let setCard = useContext(CardShopDispatcher)

    const incrementQty = (id) => {
        const clone = [...card]

        const selectedIndex = clone.find(card => card.id == id)

        console.log(selectedIndex);

        selectedIndex.qty += 1

        setCard([...clone, selectedIndex])
    }

    const decremebtQty = (id) => {
        const clone = [...card]

        const selectedIndex = clone.find(card => card.id == id)

        console.log(selectedIndex);

        selectedIndex.qty -= 1

        setCard([...clone, selectedIndex])
    }

    return { incrementQty, decremebtQty }
}

